mapper.select:
SELECT a.* FROM ( SELECT b.*, rownum rn from ( SELECT member_id, password, name, phone, email, address, grade FROM member
grade IN ('VISIT', 'VIP', 'VIP')
grade = #{grade}
) b ) a WHERE rn BETWEEN #{start} AND #{end}

SELECT member_id as "memberId", password, name, phone, email, address, grade FROM member
grade IN ('VISIT', 'VIP', 'VIP')
grade = #{grade}

mapper.update:
UPDATE member
password = #{password}, phone = #{phone}, email = #{email}, address = #{address}, grade = #{grade},
WHERE member_id = #{memberId}

mapper.select:
SELECT count(*)
  FROM member
 WHERE member_id = #{memberId}

SELECT count(*)
  FROM member
 WHERE member_id = #{memberId}
   AND password = #{password}

SELECT count(*) FROM member
grade IN ('VISIT', 'VIP', 'VIP')
grade = #{grade}

mapper.update:
UPDATE coupon
coupon_sort = #{couponSort}, grade_coupon = #{gradeCoupon}, funding_coupon = #{fundingCoupon}, coupon_used = #{couponUsed},
WHERE member_id = #{memberId} AND coupon_id = #{couponId}
