mapper.select:

SELECT *
FROM AUCTION
WHERE AUCTION_ID = #{auctionId}

mapper.update:
UPDATE AUCTION
SET AUCTION_BEST_PRICE=#{auctionPay}
WHERE AUCTION_ID=#{auctionId}

UPDATE AUCTION_JOIN
SET
AUCTION_ADDRESS=#{auctionAddress}
WHERE
AUCTION_JOIN_ID=#{auctionJoinId}

UPDATE AUCTION SET
AUCTION_CONDITION=#{auctionCondition}, AUCTION_UPDATE_DATE=SYSDATE
WHERE AUCTION_ID=#{auctionId}
